// import React, { useState } from "react";
import {
    Text,
    StyleSheet,
    View,
    TextInput,
    TouchableOpacity,
    Platform,
    KeyboardAvoidingView,
    TouchableWithoutFeedback,
    Keyboard,
    ImageBackground,
} from "react-native";
import BackgroundImage from "../../assets/img/photo-bg.jpg";
import CustomStatusBar from "../CustomStatusBar/CustomStatusBar";


const RegistrationScreen =() => {


    return (
        <ImageBackground style = {styles.background} source={BackgroundImage}>
            <CustomStatusBar/>

    <View style = {styles.registration}>
    <Text style={styles.title}>Реєстрація</Text>

    <View style={styles.form}>
      <TextInput
        style={styles.input}
        placeholder="Логін"
        placeholderTextColor="#bdbdbd"

      />
      <TextInput
        style={styles.input}
        placeholder="Адреса електронної пошти"
        placeholderTextColor="#bdbdbd"

      />
      <TextInput
        style={styles.input}
        placeholder="Пароль"
        placeholderTextColor="#bdbdbd"
        // secureTextEntry={true}

      />
       <View style = {styles.btnWrapp}>
           <TouchableOpacity style = { styles.regBtn}>
                <Text style = { styles.regBtn__text}>
                    Зареєстуватися
                </Text>
            </TouchableOpacity >
           <TouchableOpacity style = { styles.alreadyHaveAccountText}>
                <Text style = { styles.alreadyHaveAccountText}>
                    Зареєстуватися
                </Text>
            </TouchableOpacity >

           
       </View>

    </View>

            </View>
        

      </ImageBackground>
    )
}
export default  RegistrationScreen

const styles = StyleSheet.create({
    background: {
        width:'100vw',
        minHeight:'100vh',
       
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
        backgroundSize: 'cover',

        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
    },

    registration: {
        display: 'flex',
        flexDirection: 'column',
        gap: 32,
        alignItems: 'center',
        backgroundColor: '#fff',
        width: '100%',
        height: 549,
        borderTopLeftRadius: '30px',
        borderTopRightRadius: '30px',
        borderBottomLeftRadius: 0,
        borderBottomRightRadius: 0,
        paddingTop: '92px',
        paddingRight: '16px',
        paddingBottom: '8px',
        paddingLeft: '16px',
    },
    title: {
        color: '#212121',
        fontFamily: 'Roboto',
        fontSize: 30,
        fontWeight: 500,
        textAlign: 'center',
    },
    input: {
       
        width: 343,
        height: 50,
        backgroundColor: '#e8e8e8',
        borderRadius: 8,
        padding: 16,
        color: '#212121',
        borderWidth: 1,
         borderColor: "#bdbdbd",

        outlineColor: 'orange', // Outline color when focused
        outlineWidth: 1, // Outline width when focused
     
    },
    alreadyHaveAccount: {

    },
    alreadyHaveAccountText: {

    },
    form: {
        display: 'flex',
        flexDirection: 'column',
        gap: 16,
        alignItems: 'center',
    },
    btnWrapp: {
        display: 'flex',
        flexDirection: 'column',
        gap: 16,
    },
    regBtn: {
        backgroundColor: '#FF6C00',
        height: 51,
        width: 343,
        borderRadius: 25,
        padding: 16,
        textAlign: 'center',
  
    },
    regBtn__text: {
        color: '#eee',
    }
  });
