import React, { useEffect, useState } from 'react';
import {  View, Text, StyleSheet } from 'react-native';
import { Feather } from '@expo/vector-icons';
import { MaterialCommunityIcons } from '@expo/vector-icons'; 
import { Ionicons } from '@expo/vector-icons'; 
import moment from 'moment';


const CustomStatusBar = () => {

const [currentTime, setCurrentTime] = useState(moment().format('HH:mm'));

useEffect(() => {
  const timer = setInterval(() => {
    setCurrentTime(moment().format('HH:mm'));
  }, 1000);

  return () => {
    clearInterval(timer);
  };
}, []);


  return (
    <View style={styles.container}>
      <View style={styles.leftContainer}>

        <Text style={styles.text}>{currentTime}</Text>
      </View>
      <View style={styles.centerContainer}>
        <Feather name="headphones" size={20} color="#eee" />
        <View style={styles.divider} />
      </View>
      <View style={styles.rightContainer}>

      <View style={styles.signalContainer}>
        <MaterialCommunityIcons name="signal-cellular-3" size={24} color="black" />
        </View>

      <MaterialCommunityIcons name="wifi" size={24} color="#000" />
   
        <Ionicons name="battery-full-sharp" size={24} color="#000" />

      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-around',
    height: 30,
    paddingHorizontal: 16,
    backgroundColor: 'transparent',
  },
  leftContainer: {
    width:90,
    flexDirection: 'row',
    alignItems: 'center',
    gap: 8,
    
  },
  centerContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor:'#000',
    width: 219,
    height: 30,
    borderTopLeftRadius: 0,
    borderTopRightRadius: 0,
    borderBottomLeftRadius: '30px',
    borderBottomRightRadius: '30px',
  },
  rightContainer: {
    width:90,
    flexDirection: 'row',
    alignItems: 'center',
    gap: 8,
  },
  divider: {
    width: 1,
    height: 12,
    backgroundColor: '#000',
    
  },
  signalContainer: {
    flexDirection: 'row',
    alignItems: 'center',
   
  },
  signalBar: {
    height: 4,
    backgroundColor: '#000',
  
  },
  text: {
   
    color: '#000',
    fontSize: 18,
    fontWeight:800,
  },
});

export default CustomStatusBar;
