import React from 'react';
import { StatusBar, View, Text, StyleSheet } from 'react-native';
import { Feather } from '@expo/vector-icons';

const CustomStatusBar = () => {
  // Example data for signal, Wi-Fi, and battery levels
  const signalLevel = 3;
  const wifiLevel = 75;
  const batteryLevel = 80;

  return (
    <View style={styles.container}>
      <View style={styles.leftContainer}>
        <Feather name="clock" size={20} color="white" />
        <Text style={styles.text}>9:41 AM</Text>
      </View>
      <View style={styles.centerContainer}>
        <Feather name="headphones" size={20} color="white" />
        <View style={styles.divider} />
      </View>
      <View style={styles.rightContainer}>
        <Feather name="wifi" size={20} color="white" />
        <Text style={styles.text}>{wifiLevel}%</Text>
        <View style={styles.signalContainer}>
          <Feather name="signal" size={20} color="white" />
          <View style={[styles.signalBar, { width: signalLevel * 8 }]} />
        </View>
        <Feather name="battery" size={20} color="white" />
        <Text style={styles.text}>{batteryLevel}%</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    height: 24,
    paddingHorizontal: 16,
    backgroundColor: 'transparent',
  },
  leftContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  centerContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor:'#000',
    width:219,
    height:30,
    borderRadius: ['4px' ,'rem' ,'4px',],
  },
  rightContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  divider: {
    width: 1,
    height: 12,
    backgroundColor: 'white',
    marginHorizontal: 8,
  },
  signalContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginLeft: 8,
  },
  signalBar: {
    height: 4,
    backgroundColor: 'white',
    marginLeft: 2,
  },
  text: {
    marginLeft: 4,
    color: 'white',
    fontSize: 12,
  },
});

export default CustomStatusBar;
