// import React, { useState } from "react";
import {
    Text,
    StyleSheet,
    View,
    TextInput,
    TouchableOpacity,
    Platform,
    KeyboardAvoidingView,
    TouchableWithoutFeedback,
    Keyboard,
    ImageBackground,
 
} from "react-native";

import User from "../../assets/img/user.png";
import CustomStatusBar from "../CustomStatusBar/CustomStatusBar";
import { MaterialCommunityIcons } from '@expo/vector-icons'; 
import { Image } from "react-native";

const PostsScreen =() => {

    return (
        <View style = {styles.background}>
        <CustomStatusBar/>

        <View style = {styles.postsScreen}>
        <View style={styles.titleWrapp}>
            <Text style={styles.title}>
              Публікації
            </Text>
            <TouchableOpacity style={styles.trayArrowBtn}>
            <MaterialCommunityIcons style = {styles.trayArrow} name="tray-arrow-up" size={24} color="black" />
            </TouchableOpacity>
        </View>

        <View>
            <Image style={styles.user} source={User} />  
            <View>
            <Text style={styles.alreadyHaveAccountText}>Natali Romanova</Text>
            <Text style={styles.alreadyHaveAccountText}>email@example.com</Text>

            </View>
        </View>
        </View>
        </View>
        
    )
}
export default PostsScreen

const styles = StyleSheet.create({
    background: {
        width:'100vw',
        minHeight:'100vh',
       
        backgroundColor: '#fff',
        display: 'flex',
        flexDirection: 'column',
        
    },

    postsScreen: {

    },
    titleWrapp: {
        position: 'relative',
        height: 44,

        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        borderBottomWidth: 1,
        borderBottomColor: '#777',
        borderBottomStyle: 'solid',
        
    },
    title: {
        color: '#212121',
        fontFamily: 'Roboto',
        fontSize: 17,
        fontWeight: 500,
        textAlign: 'center',
    },
    trayArrowBtn: {
        position: 'absolute',
        right: 16,
    },
    trayArrow: {
        transform: 'rotate(90deg)' ,
        color: '#bdbdbd',
    },
    user: {
        
    }
})
