import React, { useEffect, useState } from 'react';
import { StatusBar, View, Text, StyleSheet } from 'react-native';
import { Feather } from '@expo/vector-icons';
import { MaterialCommunityIcons } from '@expo/vector-icons'; 
import { Ionicons } from '@expo/vector-icons'; 
import moment from 'moment';


const CustomStatusBar = () => {
  // Example data for signal, Wi-Fi, and battery levels
//   const signalLevel = 3;
//   const wifiLevel = 75;
//   const batteryLevel = 80;
const [currentTime, setCurrentTime] = useState(moment().format('h:mm A'));

useEffect(() => {
  const timer = setInterval(() => {
    setCurrentTime(moment().format('h:mm A'));
  }, 1000);

  return () => {
    clearInterval(timer);
  };
}, []);


  return (
    <View style={styles.container}>
      <View style={styles.leftContainer}>
        {/* <Feather name="clock" size={20} color="#000" /> */}
        {/* <Text style={styles.text}>9:41 AM</Text> */}
        <Text style={styles.text}>{currentTime}</Text>
      </View>
      <View style={styles.centerContainer}>
        <Feather name="headphones" size={20} color="#000" />
        <View style={styles.divider} />
      </View>
      <View style={styles.rightContainer}>

      <View style={styles.signalContainer}>
        <MaterialCommunityIcons name="signal-cellular-3" size={24} color="black" />
          {/* <View style={[styles.signalBar, { width: signalLevel * 8 }]} /> */}
        </View>

      <MaterialCommunityIcons name="wifi" size={24} color="black" />
        {/* <Text style={styles.text}>{wifiLevel}%</Text> */}
        
        <Ionicons name="battery-full-sharp" size={24} color="black" />
        {/* <Text style={styles.text}>{batteryLevel}%</Text> */}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-around',
    height: 30,
    paddingHorizontal: 16,
    backgroundColor: 'transparent',
  },
  leftContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    gap: 8,
    fontSize:22,
  },
  centerContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor:'#000',
    width: 219,
    height: 30,
    borderRadius: ['4px' ,'40px' ,'4px',],
  },
  rightContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    gap: 8,
  },
  divider: {
    width: 1,
    height: 12,
    backgroundColor: '#000',
    
  },
  signalContainer: {
    flexDirection: 'row',
    alignItems: 'center',
   
  },
  signalBar: {
    height: 4,
    backgroundColor: '#000',
  
  },
  text: {
   
    color: '#000',
    fontSize: 12,
  },
});

export default CustomStatusBar;
